+    background: #663333;
+    border-color: #884444;
+    color: #ffaaaa;
+}
+
+.options-btn.reset-btn:hover {
+    background: #884444;
+    border-color: #aa6666;
+}
+
+.version-item {
+    font-size: 0.75rem;
+    color: #666;
+    padding: 4px 0;
+}
+
+.version-label {
+    color: #888;
+}
+
+.version-value {
+    font-family: monospace;
+    color: #666;
+}
